import React, { useState, useEffect } from 'react';
import config from '../config'; // Import your config file for the host URL
import './Profile.css';

function Profile() {
    const [activeTab, setActiveTab] = useState('listings'); // "listings" or "biddings"
    const [listings, setListings] = useState([]);

    useEffect(() => {
        if (activeTab === 'listings') {
            fetchUserListings();
        }
    }, [activeTab]);

    const fetchUserListings = async () => {
        const token = localStorage.getItem('jwtToken');

        if (!token) {
            console.error('No token found');
            return;
        }

        try {
            const response = await fetch(`${config.API_BASE_URL}/get-user-listing`, {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                throw new Error(`Failed to fetch listings. Status: ${response.status}`);
            }

            const data = await response.json();
            setListings(data.listings); // Assuming the response contains the listings in a key called 'listings'
        } catch (error) {
            console.error('Error fetching listings:', error);
        }
    };

    return (
        <div className="profile-page">
            <h1>Profile</h1>

            <div className="tabs">
                <button
                    className={activeTab === 'listings' ? 'active' : ''}
                    onClick={() => setActiveTab('listings')}
                >
                    My Listings
                </button>
                <button
                    className={activeTab === 'biddings' ? 'active' : ''}
                    onClick={() => setActiveTab('biddings')}
                >
                    My Biddings
                </button>
            </div>

            <div className="tab-content">
                {activeTab === 'listings' && (
                    <div className="listings-section">
                        <h2>My Listings</h2>
                        {listings.length === 0 ? (
                            <p>You don't have any listings yet.</p>
                        ) : (
                            <ul>
                                {listings.map((listing, index) => (
                                    <li key={index}>
                                        <p><strong>Event:</strong> {listing.eventName}</p>
                                        <p><strong>Date:</strong> {new Date(listing.eventDate).toLocaleDateString()}</p>
                                        <p><strong>Seats:</strong> {listing.seats.map(seat => `#${seat.seatNumber} (Block: ${seat.block}, Level: ${seat.level})`).join(', ')}</p>
                                        <p><strong>Price:</strong> â‚¹{listing.price}</p>
                                    </li>
                                ))}
                            </ul>
                        )}
                    </div>
                )}

                {activeTab === 'biddings' && (
                    <div className="biddings-section">
                        <h2>My Biddings</h2>
                        <p>Biddings functionality will be available soon.</p>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Profile;
