import React, { useState, useEffect } from 'react';
import { Tab } from '@headlessui/react';
import { Calendar, User, MapPin, CreditCard, Clock } from 'lucide-react';
import dayjs from 'dayjs';

const Profile = () => {
    const [tickets, setTickets] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchUserListings();
    }, []);

    const fetchUserListings = async () => {
        setLoading(true);
        setError(null);
        const token = localStorage.getItem('jwtToken');

        if (!token) {
            console.error('No token found');
            return;
        }

        try {
            const response = await fetch(`${process.env.API_BASE_URL}/get-user-listing`, {
                method: 'GET',
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });

            if (!response.ok) {
                throw new Error(`Failed to fetch listings. Status: ${response.status}`);
            }

            const data = await response.json();
            setTickets(data.Tickets || []);
        } catch (error) {
            console.error('Error fetching listings:', error);
            setError('Failed to fetch listings.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="max-w-4xl mx-auto p-6">
            <h1 className="text-3xl font-bold text-center mb-8">Profile</h1>

            <Tab.Group>
                <Tab.List className="flex space-x-1 rounded-xl bg-blue-900/20 p-1">
                    {['My Listings', 'My Biddings'].map((category) => (
                        <Tab
                            key={category}
                            className={({ selected }) =>
                                `w-full rounded-lg py-2.5 text-sm font-medium leading-5 text-blue-700
                ring-white ring-opacity-60 ring-offset-2 ring-offset-blue-400 focus:outline-none focus:ring-2
                ${selected
                                    ? 'bg-white shadow'
                                    : 'text-blue-100 hover:bg-white/[0.12] hover:text-white'
                                }`
                            }
                        >
                            {category}
                        </Tab>
                    ))}
                </Tab.List>
                <Tab.Panels className="mt-2">
                    <Tab.Panel className="rounded-xl bg-white p-3">
                        <h2 className="text-2xl font-semibold mb-4">My Listings</h2>
                        {loading ? (
                            <p className="text-center">Loading...</p>
                        ) : error ? (
                            <p className="text-red-500 text-center">{error}</p>
                        ) : tickets.length === 0 ? (
                            <p className="text-center text-gray-500">You don't have any listings yet.</p>
                        ) : (
                            <div className="space-y-4">
                                {tickets.map((ticket, index) => (
                                    <div key={index} className="bg-gray-50 rounded-lg p-4 shadow">
                                        <div className="grid grid-cols-2 gap-4">
                                            <div className="flex items-center">
                                                <Calendar className="mr-2 h-5 w-5 text-gray-400" />
                                                <span className="text-sm text-gray-600">Event Date:</span>
                                                <span className="ml-2 font-medium">
                                                    {new Date(ticket.eventDate).toLocaleDateString()}
                                                </span>
                                            </div>
                                            <div className="flex items-center">
                                                <User className="mr-2 h-5 w-5 text-gray-400" />
                                                <span className="text-sm text-gray-600">Listed By:</span>
                                                <span className="ml-2 font-medium">{ticket.listedBy}</span>
                                            </div>
                                            <div className="col-span-2">
                                                <div className="flex items-center mb-2">
                                                    <MapPin className="mr-2 h-5 w-5 text-gray-400" />
                                                    <span className="text-sm text-gray-600">Seats:</span>
                                                </div>
                                                <ul className="list-disc list-inside pl-5 space-y-1">
                                                    {ticket.seatInfo.map((seat, seatIndex) => (
                                                        <li key={seatIndex} className="text-sm">
                                                            Seat {seat.seatNumber} (Block: {seat.block}, Level: {seat.level})
                                                        </li>
                                                    ))}
                                                </ul>
                                            </div>
                                            <div className="flex items-center">
                                                <CreditCard className="mr-2 h-5 w-5 text-gray-400" />
                                                <span className="text-sm text-gray-600">Price:</span>
                                                <span className="ml-2 font-medium">â‚¹{ticket.price}</span>
                                            </div>
                                            <div className="flex items-center">
                                                <Clock className="mr-2 h-5 w-5 text-gray-400" />
                                                <span className="text-sm text-gray-600">Deadline:</span>
                                                <span className="ml-2 font-medium">
                                                    {dayjs(ticket.deadline).isValid()
                                                        ? dayjs(ticket.deadline).format('DD/MM/YYYY')
                                                        : new Date(ticket.eventDate).toLocaleDateString()}
                                                </span>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </Tab.Panel>
                    <Tab.Panel className="rounded-xl bg-white p-3">
                        <h2 className="text-2xl font-semibold mb-4">My Biddings</h2>
                        <p className="text-center text-gray-500">Biddings functionality will be available soon.</p>
                    </Tab.Panel>
                </Tab.Panels>
            </Tab.Group>
        </div>
    );
};

export default Profile;
