import React, { useEffect, useState } from 'react';
import config from '../config'; // Assuming config contains the host
import './EventsSection.css';

function EventsSection({ isLoggedIn }) {
    const [tickets, setTickets] = useState([]);
    const [view, setView] = useState('block'); // 'block' or 'list'
    const [blockFilter, setBlockFilter] = useState('');
    const [levelFilter, setLevelFilter] = useState('');
    const [sortOption, setSortOption] = useState('deadline'); // Can be 'deadline', 'price-asc', 'price-desc'

    useEffect(() => {
        if (isLoggedIn) {
            fetchTickets();
        }
    }, [isLoggedIn]);

    const fetchTickets = async () => {
        const token = localStorage.getItem('jwtToken');
        const response = await fetch(`${config.API_BASE_URL}/get-all-tickets`, {
            method: 'GET',
            headers: {
                Authorization: `Bearer ${token}`,
            },
        });

        if (response.ok) {
            const data = await response.json();
            setTickets(data.Tickets);
        }
    };

    const filterTickets = (tickets) => {
        return tickets.filter(ticket => {
            const matchesBlock = blockFilter ? ticket.seatInfo.some(seat => seat.block === blockFilter) : true;
            const matchesLevel = levelFilter ? ticket.seatInfo.some(seat => seat.level === parseInt(levelFilter)) : true;
            return matchesBlock && matchesLevel;
        });
    };

    const sortTickets = (tickets) => {
        switch (sortOption) {
            case 'deadline':
                return tickets.sort((a, b) => new Date(a.deadline) - new Date(b.deadline));
            case 'price-asc':
                return tickets.sort((a, b) => a.price - b.price);
            case 'price-desc':
                return tickets.sort((a, b) => b.price - a.price);
            default:
                return tickets;
        }
    };

    const renderTickets = () => {
        let filteredTickets = filterTickets(tickets);
        let sortedTickets = sortTickets(filteredTickets);

        return sortedTickets.map((ticket, index) => (
            <div key={index} className={view === 'block' ? 'ticket-block' : 'ticket-list'}>
                <h3>Coldplay Music of The Spheres World Tour</h3>
                <p>Date: {new Date(ticket.eventDate).toLocaleDateString()}</p>
                <p>Price: â‚¹{ticket.price}</p>
                <p>Seats: {ticket.seatInfo.map(seat => `#${seat.seatNumber} (Block: ${seat.block}, Level: ${seat.level})`).join(', ')}</p>
                <p>Time Left: {calculateTimeLeft(ticket.deadline)}</p>
                <button>View Details</button>
            </div>
        ));
    };

    const calculateTimeLeft = (deadline) => {
        const difference = new Date(deadline) - new Date();
        const days = Math.floor(difference / (1000 * 60 * 60 * 24));
        const hours = Math.floor((difference / (1000 * 60 * 60)) % 24);
        const minutes = Math.floor((difference / 1000 / 60) % 60);
        const seconds = Math.floor((difference / 1000) % 60);
        return `${days}d ${hours}h ${minutes}m ${seconds}s`;
    };

    return (
        <div className="events-section">
            <h2>Coldplay Music of The Spheres World Tour</h2>

            {/* Filter and Sorting Options */}
            <div className="filter-sort">
                <select onChange={(e) => setSortOption(e.target.value)}>
                    <option value="deadline">Ending Soon</option>
                    <option value="price-asc">Price Low to High</option>
                    <option value="price-desc">Price High to Low</option>
                </select>

                <select onChange={(e) => setBlockFilter(e.target.value)}>
                    <option value="">All Blocks</option>
                    <option value="A">Block A</option>
                    <option value="B">Block B</option>
                    <option value="C">Block C</option>
                    {/* Add more block options as needed */}
                </select>

                <select onChange={(e) => setLevelFilter(e.target.value)}>
                    <option value="">All Levels</option>
                    <option value="1">Level 1</option>
                    <option value="2">Level 2</option>
                    <option value="3">Level 3</option>
                </select>

                {/* View Toggle */}
                <button onClick={() => setView('block')}>Block View</button>
                <button onClick={() => setView('list')}>List View</button>
            </div>

            {/* Render Tickets */}
            <div className={`ticket-container ${view}`}>
                {renderTickets()}
            </div>
        </div>
    );
}

export default EventsSection;
